async function getData(reqUrl, authToken) {
    const response = await fetch(reqUrl, {
        method: "GET",
        headers: {
            "Content-type": "application/json",
            Accept: "application/json",
            Authorization: "Bearer " + authToken,
        },
    });
    const data = await response.json();
    return data;
}

